import { StyleSheet, Text, View } from "react-native";
import { Feather } from "@expo/vector-icons";
import { colors } from "../Global/Colors";
import { useSelector } from "react-redux";
import React, { useState } from "react";



const OrderItem2 = ({title, price,quantity, subTotal}) => {


    // console.warn(" xxxxxxxxxxxxxxxxx ", title);
    // console.warn(" xxxxxxxxxxxxxxxxx ", order);
    // console.warn(" xxxxxxxxxxxxxxxxx ", totalorder);
    // console.warn(" xxxxxxxxxxxxxxxxx ", numberOrder);
    // console.warn(" xxxxxxxxxxxxxxxxx ", updatedAt);





    return (
        <>
            <View  onPress={() => { }}>
                <View >
                    <Text style={styles.text1}> {quantity} units of {title} |  ${price}   </Text>
                    <Text style={styles.text4}> Subtotal${subTotal}</Text> 
                </View>
            </View>
        </>
    );
};



export default OrderItem2;

const styles = StyleSheet.create({
    card: {
        height: "min-content",
        display: "flex",
        flexDirection: "column",
        backgroundColor: colors.peach,
        padding: 10,
        margin: 10,
        borderWidth: 2,
        borderRadius: 10,
        justifyContent: "space-between",
        alignItems: "center",
    },
    card2: {
        height: "min-content",
        backgroundColor: "white",
        padding: 10,
        margin: 10,
        borderWidth: 2,
        borderRadius: 10,
        flexDirection: "column",
        justifyContent: "space-between",
        alignItems: "center",
    },
    textContainer: {
        width: "70%",
        flexDirection: "column",
        justifyContent: "flex-start",
        alignItems: "flex-start",
    },
    text: {
        fontFamily: "Josefin",
        fontSize: 17,
        color: "black",
    },
    text1: {
        fontFamily: "Josefin",
        fontSize: 22,
        color: "gray"
    },
    text2: {
        fontFamily: "Josefin",
        fontSize: 19,
        color: "gray",
    },
    text4: { 
        fontFamily: "Josefin",
        fontSize: 22,
        color: "white",  
    },
});

