import { FlatList, Pressable, StyleSheet, Text, View } from 'react-native'
import React from 'react'
import CartItem from '../Components/CartItem';
import { useSelector } from 'react-redux';
import { usePostCartMutation } from '../Services/shopServices';
import Home from './Home';
import { useDispatch } from "react-redux";
import { removeCartItem } from "../features/Cart/cartSlice";
import { addOrder } from "../features/Orders/orders";


const Cart = () => {
    const {items: CartData, total, updatedAt, user} = useSelector(state => state.cartReducer.value)
    const [triggerPostCart, result] = usePostCartMutation()

    const dispatch = useDispatch()

    const onConfirm = () => {
        triggerPostCart({items: CartData, total, user, updatedAt})
        alert("ORDEN CONFIRMADA- GRACIAS POR COMPRAR") 
        
                console.warn("ORDENES ?", CartData);
                dispatch(addOrder({
                    CartData
                })) 
                dispatch(removeCartItem({ })) 


    }

    console.log(result);

    return (
    <View style={styles.container}> 
    {console.warn("TOTAL", total)}
           {total === 0  ? (
            <></>
           ) : (<>
             <FlatList
            data={CartData}
            keyExtractor={cartItem => cartItem.id}
            renderItem={({item})=> {
                return (
                    <CartItem
                        cartItem={item}
                    />
                )
            }}
        />
        <View style={styles.totalContainer}>
            <Pressable
                onPress = {onConfirm}
            >
                <Text>
                    Confirm
                </Text>
            </Pressable>
            <Text>Total: ${total}</Text>
        </View>
           </>)
       } 
     
    </View>
  )
}

export default Cart

const styles = StyleSheet.create({
    container: {
        justifyContent: 'space-between',
        flex: 1,
    },
    totalContainer: {
        flexDirection: 'row',
        justifyContent: 'space-around',
        alignItems: 'center',
        paddingBottom: 20,
    }
})


