import { FlatList, StyleSheet, Text, View } from 'react-native'
import React from 'react'
import OrderData from '../Data/orders.json'
import OrderItem from '../Components/OrdenItem'
import OrderItem2 from '../Components/OrdenItem2'

import { useSelector } from "react-redux";

const OrderScreen = () => {
  const {items : CartData} = useSelector(state => state.orderReducer.value)
  const infoData = CartData
console.warn("cardata desde ordenscreen", infoData);
console.warn("cardata desde ordenscreen", infoData.CartData);
console.warn("ssssssssssss", CartData[0].CartData.length);

let totalorder = 0 
for (let i=0; i<CartData[0].CartData.length ; i++){
totalorder += CartData[0].CartData[i].price * CartData[0].CartData[i].quantity;
}
console.warn("AVERRRRRRRRRRR,",totalorder);
  return (
    <View> 
      {/* <OrderItem  order={CartData} totalorder={totalorder} /> */}
        <FlatList
            data={CartData}
            keyExtractor={orderItem => orderItem.id}
            renderItem={({
              infoData
            }) => {
                return (
                    <OrderItem2
                      order={CartData} totalorder={totalorder}
                    />
                )
            }}
        />
    </View>
  )
}

export default OrderScreen

const styles = StyleSheet.create({})

